"use strict";

var decorateTypeDef = require("../decorateTypeDef");

module.exports = function exactString(str) {
  return decorateTypeDef({
    description: JSON.stringify(str),
    serializedDescription: "{ $type: \"string\", $value: ".concat(JSON.stringify(str), " }"),
    check: function check(val) {
      return val === str;
    },
    serialize: function serialize(val) {
      return {
        $type: "string",
        $value: val
      };
    },
    checkSerialized: function checkSerialized(serialized) {
      return serialized.$type === "string" && serialized.$value === str;
    },
    deserialize: function deserialize(serialized) {
      return serialized.$value;
    }
  });
};