"use strict";

var decorateTypeDef = require("../decorateTypeDef");

module.exports = decorateTypeDef({
  description: "Date",
  serializedDescription: '{ $type: "Date", $value: number }',
  check: function check(val) {
    return val instanceof Date;
  },
  serialize: function serialize(date) {
    return {
      $type: "Date",
      $value: Number(date)
    };
  },
  checkSerialized: function checkSerialized(serialized) {
    return serialized.$type === "Date";
  },
  deserialize: function deserialize(serialized) {
    return new Date(serialized.$value);
  }
});