"use strict";

var decorateTypeDef = require("../decorateTypeDef");

module.exports = decorateTypeDef({
  description: "integer",
  serializedDescription: '{ $type: "integer", $value: number }',
  check: function check(val) {
    return typeof val === "number" && parseInt(String(val)) === val;
  },
  serialize: function serialize(integer) {
    return {
      $type: "integer",
      $value: integer
    };
  },
  checkSerialized: function checkSerialized(serialized) {
    return serialized.$type === "integer";
  },
  deserialize: function deserialize(serialized) {
    return serialized.$value;
  }
});