"use strict";

var decorateTypeDef = require("../decorateTypeDef");

module.exports = decorateTypeDef({
  description: "number",
  serializedDescription: '{ $type: "number", $value: number }',
  check: function check(val) {
    return typeof val === "number" && !Number.isNaN(val) && val !== Infinity && val !== -Infinity;
  },
  serialize: function serialize(number) {
    return {
      $type: "number",
      $value: number
    };
  },
  checkSerialized: function checkSerialized(serialized) {
    return serialized.$type === "number";
  },
  deserialize: function deserialize(serialized) {
    return serialized.$value;
  }
});