"use strict";

var decorateTypeDef = require("../decorateTypeDef");

module.exports = decorateTypeDef({
  description: "string",
  serializedDescription: '{ $type: "string", $value: string }',
  check: function check(val) {
    return typeof val === "string";
  },
  serialize: function serialize(str) {
    return {
      $type: "string",
      $value: str
    };
  },
  checkSerialized: function checkSerialized(serialized) {
    return serialized.$type === "string";
  },
  deserialize: function deserialize(serialized) {
    return serialized.$value;
  }
});